        throw INVALIDARGUMENT(aSrcDstChannel2, "Not all source image planes have the same image pitch. First image pitch is " << aSrcDstChannel0.Pitch() << " but the pitch for plane no 2 is " << aSrcDstChannel2.Pitch());
